import React, {useEffect, useRef, useState} from "react";
//import { useHorizontalScroll } from '../hooks/useHorizontalSroll';
import Images from "../data/Images";


function Gallery(props) {
    /* 함수 사용하는 세팅법
    const imagesRef = useRef([]);
    useEffect(() => {
        imagesRef.current = imagesRef.current.slice(0, Images.length);
    });
    function createImages() {
        let arr = [];
        for (let i=0; i <Images.length; i++) {
            arr.push(
                <div key={i} className="image" ref={el => imagesRef.current[i] = el}>
                </div>
            )
        }
        console.log(arr);
        return arr;
    }
    function setImages() {
        for (let i=0; i <Images.length; i++) {
            imagesRef.current[i].style.backgroundImage= "url('../images/gallery/image2')";
        }
    }
    */
    const wrapRef = useRef(null);
    const secRef = useRef(null);
    useEffect(()=>{
        console.log(props.galleryIndex);
        console.log(wrapRef.current.getBoundingClientRect().width/3*props.galleryIndex);
        console.log(window.scrollX);
        if(wrapRef.current) {
            //window.scrollTo(wrapRef.current.getBoundingClientRect().width/3*props.galleryIndex, 0);
            wrapRef.current.scrollTo({
                top: 0,
                left: wrapRef.current.getBoundingClientRect().width/3*props.galleryIndex,
                behavior: "smooth"
              });

              secRef.current.scrollTo({
                top: 0,
                left: wrapRef.current.getBoundingClientRect().width/3*props.galleryIndex,
                behavior: "smooth"
              });
        }

        
    }, [props.galleryIndex]);

    var [current, setCurrent] = useState(0);
    const horizontalScrollRef = useRef(null);
    const handleNextButtonClick = () => {
        const nextType = "next";
        if (!horizontalScrollRef.current) return;
          if (nextType === 'prev') {
            horizontalScrollRef.current.scrollTo({
              left: horizontalScrollRef.current.scrollLeft - horizontalScrollRef.current.offsetWidth,
              behavior: 'smooth',
            });
          } else {
            //window.scrollTo(500, 0);
            horizontalScrollRef.current.scrollTo({
              left: horizontalScrollRef.current.scrollLeft + 500,
              behavior: 'smooth',
            });
          }
      };
      function useHorizontalScroll() {
        const elRef = useRef();
        useEffect(() => {
          const el = elRef.current;
          if (el) {
            const onWheel = e => {
              if (e.deltaY == 0) return;
              e.preventDefault();
              if(e.deltaY>0) {
                window.scrollTo(window.scrollX+el.getBoundingClientRect().width/3, 0);
                console.log(current);
                let next = current + 1;
                setCurrent(next);
                console.log("here");
                console.log(current);
              }
              else {
                window.scrollTo(window.scrollX-el.getBoundingClientRect().width/3, 0);
                setCurrent(current-1);
                console.log(current);

              }

              /*
              el.scrollTo({
                top: 0,
                left: el.scrollLeft + e.deltaY,
                behavior: "smooth"
              });
              */
            };
            el.addEventListener("wheel", onWheel);
            return () => el.removeEventListener("wheel", onWheel);
          }
        }, []);
        return elRef;
      }
    const scrollRef = useHorizontalScroll();
    return (
        <section className="sec_4" ref={secRef}>
            <div className="image_wrap" onWheel={function(e){props.onWheel(e.deltaY);}} ref={wrapRef}>
            {
				Images.map((val, ind) => {
                    return <div key={ind} className="image" style={{backgroundImage: val.image}}></div>              
				})
			}
            </div>
            <div className="bottom_bar">
                <p onClick={handleNextButtonClick}>{current}</p>
            </div>
		</section>
    )
    
}

export default Gallery;
