import React, {useState} from "react";
import axios from "axios";

async function Script() {
    async function api(url, method, data) {
        return (await axios({
            method: method,
            url,
            data
        }).catch(e => {
            console.log(e);
        })).data;
    };
    const getList = () => {
        // alert("getList(actionMode) =>" + actionMode.mode);
    
        // Ajax 구현
        axios
          .post("http://localhost:3000/api/getScript", {}) // {} 빈 객체 전달
          .then((res) => {
            //console.log(res);
            return res.data;
          })
          .catch((e) => {
            console.error(e);
          });
      };

    const [scriptList, setScriptList] = useState({ scriptList: [] });
    let result = await api("http://localhost:3000/api/getScript", "post", {})
    //let result = getList();
    console.log(result);
    setScriptList({scriptList: result});
    console.log(scriptList);

 
