import {createStore} from 'redux';
export default createStore(function(state, action) {
  if(state === undefined) {
      return {pageState:'intro', characterIndex: 0, galleryIndex: 0}
  }
  if(action.type === 'changePage') {
      console.log(action.page);
      return {...state, pageState: action.page}
  }
  if(action.type === 'incrementCharacter') {
    return {...state, characterIndex: state.characterIndex + action.value}
  }
  if(action.type === 'changeCharacter') {
    return {...state, characterIndex: action.value}
  }
  if(action.type === 'changeGallery') {
    return {...state, galleryIndex: state.galleryIndex + action.value}
  }
  return state;
}, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
