import React from "react";
//import NavigateBeforeIcon from '@material-ui/icons/NavigateBefore';

function Leftbar(props) {
	const onPress = (e) => {
		e.preventDefault();
		
		const target = window.document.getElementById(
		  e.currentTarget.href.split("#")[1]
		);
		console.log(target);
		if (target) {
			console.log(target);
		  target.scrollIntoView({ behavior: "smooth" });
		}
	  };
	 
        return (
            <div className="left_bar">
				{
				props.pageState=="character" && 
				  <div className="scroll_bar">
                  <div className="scroll_prev" onClick={function(){props.onClick('prev');}}><span></span><span></span><span></span></div>
                  <span className="text_rotate">SCROLL</span>
                  <div className="scroll_next" onClick={function(){props.onClick('next');}}><span></span><span></span><span></span></div>
				  </div>
				}
				{
					props.pageState=="gallery" && 
					<div className="prev_button" onClick={function(){props.onClick('gallery');}}>
						<span></span>
					</div>
				}
		</div>
        )
    
}

export default Leftbar;
