import React from "react";
import { useRef, useEffect } from 'react';
import Characters from '../data/Characters'

function Character() {
 
        return (
            <section className="sec_3"  id="scrollspyHeading3">
                <div className="character_list">
                    <ul>
                    {
							Characters.map((val, ind) => {
								return <li key={ind} style={{
                                    backgroundImage: val.thumbnail, borderLeftColor: val.color,
                                    boxShadow: "inset 30px 0 60px " + val.color}}>
                                        <p>{val.name}</p>
                                    </li>
                                
							})
						}
                    </ul>
                </div>
				<div className="character_desc">
						<h1 className="text_light">스사노오</h1>
                        <p>타카마가하라 최강의 무신.</p>
				</div>
                <div className="character_img"></div>
                <div className="next_button"><a href="#"><span></span><span></span><span></span></a></div>
		</section>
        )
    
}

export default Character;
