import React, {useState} from "react";
import {NavLink} from 'react-router-dom';
import store from "../store";

function Navbar(props) {
	//var [pageState, setPageState] = useState(store.getState().pageState);
	const onPress = (e) => {
		e.preventDefault();
		
		const target = window.document.getElementById(
		  e.currentTarget.href.split("#")[1]
		);
		console.log(target);
		if (target) {
			console.log(target);
		  target.scrollIntoView({ behavior: "smooth" });
		}
	  };
	 
        return (
            <div className="container-fluid navigation_bar">
			<nav className="navscroll navbar fixed-top navbar-expand-lg navbar-light py-0" id="navbar-example2" >
                <div className="navbar-brand">Onmyoji</div>
				<button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
				    <span className="text-white"></span>
				</button>
				<div className="collapse navbar-collapse" id="navbarSupportedContent">
				    <ul className="navbar-nav ms-auto nav">
				      <li className="nav-item my-1 align-start me-4">
					  <NavLink className="nav-link" to="/intro"><span className="d-lg-none d-xl-none me-3"></span>인트로</NavLink>
				      </li>
				      <li className="nav-item my-1 me-4">
					  <NavLink className="nav-link" to="/story"><span className="d-lg-none d-xl-none me-3"></span>스토리</NavLink>
				      </li>
				      <li className="nav-item my-1 me-4">
					  <NavLink className="nav-link" to="/character" onClick={function(){
						//setPageState('character');
						//</li>store.dipatch({type:'changePage', page:'character'});
						props.onClick('character');
					}}><span className="d-lg-none d-xl-none me-3"></span>캐릭터</NavLink>
				      </li>
				      <li className="nav-item my-1 me-4">
					  <NavLink className="nav-link" to="/gallery"><div id="nav4"><span className="d-lg-none d-xl-none me-3"></span>갤러리</div></NavLink>
				      </li>					 
				    </ul>
				</div>
                <div className="navbar_right"></div>
			</nav>
		</div>
        )
    
}

export default Navbar;
