import React from "react";
import { useEffect } from "react";
import Characters from "../data/Characters";
import useMoveScroll from "../hooks/useMoveScroll";

function Character(props) {
  const refList = [useMoveScroll(), useMoveScroll(), useMoveScroll()];
  useEffect(() => {
    console.log(props.characterIndex);
    if (refList[props.characterIndex].element) {
      refList[props.characterIndex].onMoveToElement();
    }
  }, [props.characterIndex]);

  return (
    <section className="sec_3">
      <div className="character_list">
        <ul>
          {Characters.map((val, ind) => {
            if (ind == 0) {
              return (
                <li key={ind}>
                  <div
                    style={{
                      backgroundImage: val.thumbnail,
                      borderLeftColor: val.color,
                      boxShadow: "inset 30px 0 60px " + val.color,
                    }}
                    onClick={function () {
                      props.onClick(ind);
                    }}
                  ></div>
                  <p>{val.name}</p>
                </li>
              );
            } else {
              return (
                <li key={ind}>
                  <div
                    style={{
                      backgroundImage: val.thumbnail,
                      filter: "brightness(40%)",
                    }}
                    onClick={function () {
                      props.onClick(ind);
                    }}
                  ></div>
                  <p>{val.name}</p>
                </li>
              );
            }
          })}
        </ul>
      </div>

      {Characters.map((val, ind) => {
        return (
          <div key={ind} className="character_wrap" ref={refList[ind].element}>
            <div className="character_desc">
              <h1
                className="text_light"
                style={{
                  color: val.color,
                  borderBottomColor: val.color,
                }}
              >
                {val.name}
              </h1>
              <p>{val.desc}</p>
            </div>
            <div
              className="character_img"
              style={{
                backgroundImage: val.image,
              }}
            ></div>
          </div>
        );
      })}
    </section>
  );
}

export default Character;
