import React from "react";
import Conversation from "../data/Conversation";


function Story() {
 
        return (
            <section className="sec_2">
                <div className="scene">
                    <div className="scene_bg"></div>
                    <div className="scene_text">
                        태초부터 존재했던 거대한 뱀이 어느 날 고개를 들고 태양에게 물었습니다.
                    </div>
                </div>
                <div className="conversation">
                    <div className="line orochi">
                        <div className="img"></div>
                        <p>전지전능한 아마테라스, 당신은 정말 세인들이 당신을 사랑하는 것처럼 그들을 사랑하는가?</p>
                    </div>
                    <div className="line amaterasu">
                        <div className="img"></div>
                        <p>나는 그들이 나를 사랑하는 것처럼 그들을 사랑합니다.</p>
                    </div>
                    {Conversation.map((val, ind) => {
        return (
          <div key={ind} className={val.character}>
            <div className="img"></div>
            <p>{val.line}</p>
         </div>   
        );
      })}
                </div>

		</section>
        )
    
}

export default Story;
