import React, {useEffect, useRef} from "react";
import { useHorizontalScroll } from '../hooks/useHorizontalSroll';
import Images from "../data/Images";


function Gallery() {
    const imagesRef = useRef([]);

    useEffect(() => {
        imagesRef.current = imagesRef.current.slice(0, Images.length);
    });
    function createImages() {
        let arr = [];
        for (let i=0; i <Images.length; i++) {
            arr.push(
                <div className="image" ref={el => imagesRef.current[i] = el}>
                </div>
            )
        }
        return arr;
    }
    function setImages() {
        for (let i=0; i <Images.length; i++) {
            imagesRef.current[i].style.background= Images[i].image;
        }
    }
    const horizontalScrollRef = useRef(null);
    const handleNextButtonClick = () => {
        const nextType = "next";
        if (!horizontalScrollRef.current) return;
          if (nextType === 'prev') {
            horizontalScrollRef.current.scrollTo({
              left: horizontalScrollRef.current.scrollLeft - horizontalScrollRef.current.offsetWidth,
              behavior: 'smooth',
            });
          } else {
            //window.scrollTo(500, 0);
            horizontalScrollRef.current.scrollTo({
              left: horizontalScrollRef.current.scrollLeft + 500,
              behavior: 'smooth',
            });
          }
      };
    const scrollRef = useHorizontalScroll();
    return (
        <section className="sec_4">
            <div className="image_wrap" ref={scrollRef}>
                <div className="image">
                </div>
                {createImages}
                {setImages}
            </div>
            <div className="bottom_bar">
                <p onClick={handleNextButtonClick}>천년의 수호 일러스트 1</p>
            </div>
		</section>
    )
    
}

export default Gallery;
