import React from "react";
import { useRef, useEffect } from 'react';
import Characters from '../data/Characters'
import useMoveScroll from "../hooks/useMoveScroll";

function Character() {
    //const element = useRef(null);
    const refList = [];
    //const {element, onMoveToElement} = useMoveScroll();
    useEffect= ()=>{
        for(var i; i < 3; i++){
            eval("var ref" + i + "= useRef(null)");
            refList.push(eval("ref"+i));
            console.log(refList);
        }
    }
    
    function onMoveToElement(element){
        if(element){
            console.log(element.current);
        element.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }
        
    }
        return (
            <section className="sec_3"  id="scrollspyHeading3">
                <div className="character_list">
                    <ul>
                    {
						Characters.map((val, ind) => {
                            if(ind == 0){
                                return <li key={ind}> 
                                    <div style={{
                                        backgroundImage: val.thumbnail, borderLeftColor: val.color,
                                        boxShadow: "inset 30px 0 60px " + val.color}} onClick={onMoveToElement(refList[0])}>
                                    </div>
                                    <p>{val.name}</p>
                                    </li>
                                }else{
                                    return <li key={ind}> 
                                        <div style={{
                                        backgroundImage: val.thumbnail, filter: "brightness(40%)"}} onClick={onMoveToElement(refList[ind+1])}>
                                        </div>
                                            <p>{val.name}</p>
                                        </li>
                                }
							})
						}
                    </ul>
                </div>
                
                {
						Characters.map((val, ind) => {
                            return <div key={ind} className="character_wrap" ref={refList[ind]}>
                            <div className="character_desc">
                                    <h1 className="text_light" style={{
                                        color: val.color, borderBottomColor: val.color}}>{val.name}</h1>
                                    <p>{val.desc}</p>
                            </div>
                            <div className="character_img" style={{
                                        backgroundImage: val.image}}></div>
                        </div>
							})
						}


		</section>
        )
    
}

export default Character;
