import React, {useState} from "react";
import axios from "axios";

function Script() {
    async function api(url, method, data) {
        return (await axios({
            method: method,
            url,
            data
        }).catch(e => {
            console.log(e);
        })).data;
    };
    var [scriptList, setScriptList] = useState([{line:''}]);
    const getList = () => {
        // alert("getList(actionMode) =>" + actionMode.mode);
    
        // Ajax 구현
        axios
          .post("http://localhost:3000/api/getScript", {}) // {} 빈 객체 전달
          .then((res) => {
            //console.log(res);
            //return res.data;
            const { data } = res;
            setScriptList({scriptList: data,});
          })
          .catch((e) => {
            console.error(e);
          });
      };

    
    //let result = await api("http://localhost:3000/api/getScript", "post", {})
    //let result = getList();
    //console.log(result);
    //setScriptList(result);
    //console.log(scriptList);

 
        return (
            <section className="sec_5">
               
                    <div className="img_wrap">
                        <div className="bg"></div>
                            <div className="chr1"></div>
                            <div className="chr2"></div>
                        
                        <div className="line">{scriptList[0].line}</div>
                    </div>
                
		</section>
        )
    
}

export default Script;
