import React, {useEffect, useState} from "react";
import axios from "axios";

function Script() {
    
    var [scriptList, setScriptList] = useState([{line:''}]);
    
    const getList = () => {
        // alert("getList(actionMode) =>" + actionMode.mode);
    
        // Ajax 구현
        axios
          .post("http://localhost:3000/api/getScript", {}) // {} 빈 객체 전달
          .then((res) => {
            //console.log(res);
            //return res.data;
            const { data } = res;
            console.log(data[0]);
            setScriptList({scriptList: data,});
          })
          .catch((e) => {
            console.error(e);
          });
      };

      useEffect(()=>{
        getList();
      });

 
        return (
            <section className="sec_5">
               
                    <div className="img_wrap">
                        <div className="bg"></div>
                            <div className="chr1"></div>
                            <div className="chr2"></div>
                        
                        <div className="line">{scriptList[0]}</div>
                    </div>
                
		</section>
        )
    
}

export default Script;
