import React from "react";
import { useRef, useEffect } from 'react';
import Characters from '../data/Characters'

function Character() {
    const element = useRef(null);
    const elList = [element];
    const scrollTo = (i ,e) => {
		e.preventDefault();
		const target = elList[i];
		if (target) {
			console.log(target);
		    target.current.scrollIntoView({ behavior: "smooth" });
		}
	  };
        return (
            <section className="sec_3"  id="scrollspyHeading3">
                <div className="character_list">
                    <ul>
                    {
						Characters.map((val, ind) => {
                            if(ind == 0){
                                return <li key={ind} onClick={scrollTo(0)}> 
                                    <div style={{
                                        backgroundImage: val.thumbnail, borderLeftColor: val.color,
                                        boxShadow: "inset 30px 0 60px " + val.color}}>
                                    </div>
                                    <p>{val.name}</p>
                                    </li>
                                }else{
                                    return <li key={ind}> 
                                        <div style={{
                                        backgroundImage: val.thumbnail, filter: "brightness(40%)"}}>
                                        </div>
                                            <p>{val.name}</p>
                                        </li>
                                }
							})
						}
                    </ul>
                </div>
                <div className="character_wrap" ref={element}>
                <div className="character_desc">
                                    <h1 className="text_light">테스트</h1>
                                    <p>테스트</p>
                            </div>
                            <div className="character_img"></div>
                        </div>
                {
						Characters.map((val, ind) => {
                            return <div key={ind} className="character_wrap">
                            <div className="character_desc">
                                    <h1 className="text_light" style={{
                                        color: val.color, borderBottomColor: val.color}}>{val.name}</h1>
                                    <p>{val.desc}</p>
                            </div>
                            <div className="character_img" style={{
                                        backgroundImage: val.image}}></div>
                        </div>
							})
						}
		</section>
        )
    
}

export default Character;
