import React from "react";

function Rightbar() {
	const onPress = (e) => {
		e.preventDefault();
		
		const target = window.document.getElementById(
		  e.currentTarget.href.split("#")[1]
		);
		console.log(target);
		if (target) {
			console.log(target);
		  target.scrollIntoView({ behavior: "smooth" });
		}
	  };
	 
        return (
            <div className="left_bar">
				  <div className="menu">
				    <ul className="">
				      <li className="my-1 align-start me-4">
					  <a className="" onClick={(e) => onPress(e)} href="#scrollspyHeading1" id="nav1"><span className="d-lg-none d-xl-none me-3"></span>1</a>
				      </li>
				      <li className="my-1 me-4">
					  <a className="" onClick={(e) => onPress(e)} href="#scrollspyHeading2" id="nav2"><span className="d-lg-none d-xl-none me-3"></span>Skills</a>
				      </li>
				      <li className="my-1 me-4">
					  <a className=""  onClick={(e) => onPress(e)} href="#scrollspyHeading3" id="nav3"><span className="d-lg-none d-xl-none me-3"></span>Projects</a>
				      </li>
				      <li className="my-1 me-4">
					  <a className=""  onClick={(e) => onPress(e)} href="#scrollspyHeading4"><div id="nav4"><span className="d-lg-none d-xl-none me-3"></span>About</div></a>
				      </li>					 
				    </ul>
				  </div>
		</div>
        )
    
}

export default Rightbar;
