import React, {useEffect, useState} from "react";
import axios from "axios";

function Script() {
    
    const [scriptList, setScriptList] = useState([{line:''}]);
    const [scriptIndex, setScriptIndex] = useState(0);
    
    const getList = () => {
        // alert("getList(actionMode) =>" + actionMode.mode);
    
        // Ajax 구현
        axios
          .post("http://localhost:3000/api/getScript", {}) // {} 빈 객체 전달
          .then((res) => {
            //console.log(res);
            //return res.data;
            const { data } = res;
            //console.log(data);
            setScriptList(data);
          })
          .catch((e) => {
            console.error(e);
          });
      };

      useEffect(()=>{
        getList();
        //console.log(scriptList);
      },[]);

 
      if (scriptList.length > 0) return (
            <section className="sec_5" onClick={function(){setScriptIndex(scriptIndex+1)}}>
                <div className="img_wrap">
                    <div className="bg" style={{backgroundImage: "url('assets/script/" + scriptList[scriptIndex].background+".jpg')"}}></div>
                    {scriptList[scriptIndex].character1 && scriptList[scriptIndex].talking===0 &&
                        <div className="chr1" style={{backgroundImage: "url('assets/characters/" + scriptList[scriptIndex].character1+".png')"}}></div>
                    }
                    {scriptList[scriptIndex].character1 && scriptList[scriptIndex].talking===1 &&
                        <div className="chr1" style={{backgroundImage: "url('assets/characters/" + scriptList[scriptIndex].character1+".png')", filter: "brightness(40%)"}}></div>
                    }
                    {scriptList[scriptIndex].character2 && scriptList[scriptIndex].talking===1 &&
                        <div className="chr2" style={{backgroundImage: "url('assets/characters/" + scriptList[scriptIndex].character1+".png')"}}></div>
                    }
                    <div className="line">{scriptList[scriptIndex].line}</div>
                </div>
		</section>
      )
    
}

export default Script;
