import React, {useRef} from "react";
import { useHorizontalScroll } from '../hooks/useHorizontalSroll';


function Gallery() {
    const horizontalScrollRef = useRef(null);
    const handleNextButtonClick = () => {
        console.log('here1');
        const nextType = "next";
        if (!horizontalScrollRef.current) return;
          if (nextType === 'prev') {
            horizontalScrollRef.current.scrollTo({
              left: horizontalScrollRef.current.scrollLeft - horizontalScrollRef.current.offsetWidth,
              behavior: 'smooth',
            });
          } else {
            console.log("here");
            horizontalScrollRef.current.scrollTo({
              left: horizontalScrollRef.current.scrollLeft + 500,
              behavior: 'smooth',
            });
          }
      };
    const scrollRef = useHorizontalScroll();
    return (
        <section className="sec_4">
            <div className="image_wrap" ref={horizontalScrollRef} >
                <div className="image">
                </div>
                <div className="image">
                </div>
                <div className="image">
                </div>
            </div>
            <div className="bottom_bar">
                <p onClick={handleNextButtonClick('next')}>천년의 수호 일러스트 1</p>
            </div>
		</section>
    )
    
}

export default Gallery;
