import React, { useState } from "react";
import store from '../store';

function Rightbar(props) {
	//console.log(pageState);
	//var [pageState, setPageState] = useState(props.pageState);
	const onPress = (e) => {
		e.preventDefault();
		
		const target = window.document.getElementById(
		  e.currentTarget.href.split("#")[1]
		);
		console.log(target);
		if (target) {
			console.log(target);
		  target.scrollIntoView({ behavior: "smooth" });
		}
	  };
	 
        return (
            <div className="right_bar">
				{
					props.pageState=="character" && 
					<div className="menu">
				    <ul className="">
				      <li className="">
					  <a className="active" onClick={(e) => onPress(e)} href="#scrollspyHeading1" id="nav1"><span className="">소개</span></a>
				      </li>
				      <li className="">
					  <a className="" onClick={(e) => onPress(e)} href="#scrollspyHeading2" id="nav2"><span className="">이미지</span></a>
				      </li>
				    </ul>
				  </div>
				}
				{
					props.pageState=="gallery" && 
					<div className="next_button">
						<span></span>
					</div>
				}
		</div>
        )
    
}

export default Rightbar;
