import React from "react";
import { useEffect, useRef } from "react";
import Characters from "../data/Characters";
import useMoveScroll from "../hooks/useMoveScroll"

function Character(props) {
  const refList = [useMoveScroll(), useMoveScroll(), useMoveScroll(), useMoveScroll(), useMoveScroll(), useMoveScroll()];
  const secRef = useRef(null);

  useEffect(() => {
    console.log(props.characterIndex);
    
    if (refList[props.characterIndex].element) {
      refList[props.characterIndex].onMoveToElement();

    }
    
    if (secRef.current) {  
      secRef.current.scrollTo({
        left:
          (secRef.current.getBoundingClientRect().width) *
          props.characterMenu,
        behavior: "smooth",
      });
      
    }
    
  }, [props.characterIndex, props.characterMenu]);

  return (
    <section className="sec_3"  >
      <div className="character_list">
        <ul>
          {Characters.map((val, ind) => {
            if (ind == 0) {
              return (
                <li key={ind}>
                  <div
                    style={{
                      backgroundImage: val.thumbnail,
                      borderLeftColor: val.color,
                      boxShadow: "inset 30px 0 60px " + val.color,
                    }}
                    onClick={function () {
                      props.onClick(ind);
                    }}
                  ></div>
                  <p>{val.name}</p>
                </li>
              );
            } else {
              return (
                <li key={ind}>
                  <div
                    style={{
                      backgroundImage: val.thumbnail,
                      filter: "brightness(40%)",
                    }}
                    onClick={function () {
                      props.onClick(ind);
                    }}
                  ></div>
                  <p>{val.name}</p>
                </li>
              );
            }
          })}
        </ul>
      </div>
      <div className="character_bg"></div>
      <div className="character_wrap" ref={secRef} onWheel={function (e) {
      props.onWheel(e.deltaY);
    }}>
      {Characters.map((val, ind) => {
        return (
          <div key={ind} className="character" ref={refList[ind].element}>
          <div className="character_desc">
            <div className="desc_text">
              <h1
                className="text_light"
                style={{
                  color: val.color,
                  borderBottomColor: val.color,
                }}
              >
                {val.name}
              </h1>
              <p>{val.desc}</p>
            </div>
            <div
              className="desc_img"
              style={{
                backgroundImage: val.image,
              }}
            ></div>
          </div>
          <div className="character_image">
          </div>
          </div>
        );
      })}
      </div>
    </section>
  );
}

export default Character;
