import './App.css';
import Navbar from './containers/Navbar';
import Leftbar from './containers/Leftbar';
import Rightbar from './containers/Rightbar';
import Intro from './components/Intro';
import Story from './components/Story';
import Character from './containers/Character';
import Gallery from './containers/Gallery';
import Script from './components/Script';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle.min.js";
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import { useRef } from 'react';


function App() {
  const mouseRef = useRef(null);
  const MouseWave = (e) => {
    mouseRef.style.left = e.pageX - 16  + 'px';
    mouseRef.style.top= e.pageY - 16  + 'px';
    //document.querySelector(".original").style.left= e.pageX - 16  + 'px';
    //document.querySelector(".original").style.top= e.pageY - 16  + 'px';
  };

  return (
    <Router>
      <div className="App" onMouseMove={MouseWave}>
        <div className="ripple original" ref={mouseRef}></div>

        <Navbar></Navbar>
        <Leftbar></Leftbar>
        <Rightbar></Rightbar>

        <Routes>
          <Route path="/" element={<Intro />} />
          <Route path="/intro" element={<Intro />} />
          <Route path="/story" element={<Story />} />
          <Route path="/character" element={<Character />} />
          <Route path="/gallery" element={<Gallery />} />
          <Route path="/script" element={<Script />} />
        </Routes>

      </div>
    </Router>
  );
}

export default App;
